import React from 'react';

import styled from 'styled-components';


const CategoryStyle = styled.div`
    position: relative;
    float: right;
    margin: 5px;
    padding: 5px 10px;
    border-radius: 10px;
    /* background: rgb(230, 230, 230, 0.7); */
    z-index: 2;
    height: 35px;
    text-align: center;
    font-size: 20;

    :hover {
        background: rgb(105, 182, 223, 0.6);
        cursor: pointer;
    }

    /* background: {props => (props.isActive ? 'darkred' : 'red')} */
    /* ${props => props.isActive && ({
     background: 'darkred',
     border: '1px solid limegreen',
     borderRadius: '25px'
  })} */
  ${({ isActive }) => active && `
    background: blue;
  `}
`;


class CategoryCard extends React.Component{
  constructor(props){
    super(props);
    // this.state = { isActive: false};

    // This bind is necessary to make `this` work in the callback in handleClick()
    // this.handleClick = this.handleClick.bind(this);
  }

  // handleClick(){
  //   // console.log(this)
  //   // console.log('click! from category')

  //   this.setState(state => ({
  //     isActive: !state.isActive
  //   }));
  // }

  render(){



    return(
        <CategoryStyle
          onClick={()=>this.props.onCategoryClick(this.props.category)}
        >
            {this.props.category} - 
            {JSON.stringify(this.props.isActive)}
        </CategoryStyle>
  );
  }
}

export default CategoryCard;
